function smoothScroll(){$('a[href^="#"]').on("click",function(o){var t=$($(this).attr("href"));t.length&&(o.preventDefault(),$("html, body").animate({scrollTop:t.offset().top},800))})}$(function(){smoothScroll()}),$(window).scroll(function(){var o=$("#info").offset().top;$("#logo, .sec-fo").css({opacity:1-$(this).scrollTop()/250}),$(window).bind("scroll",function(){var t=$(window).scrollTop();t>=o&&$("#main").animate({top:"0px"},300)})});